    private static boolean testEntries(String[] values, int[] results) {
        for (int i = 0; i < values.length; i++) {
            if (getFloor(values[i]) != results[i]) {
                return false;
            }
        }
        return true;
    }

    private static int getFloor(String input) {
        int floor = 0;
        for (int i = 0; i < input.length(); i++) {
            if (input.substring(i, i+1).equalsIgnoreCase(UP)) {
                floor++;
            } else if (input.substring(i, i+1).equalsIgnoreCase(DOWN)) {
                floor--;
            }
        }
        return floor;
    }
